import React, { useEffect, useState } from 'react';

import Movie from './components/Movie';
const FEATURE_API = "https://api.themoviedb.org/3/movie/550?api_key=6c509f03a0794e8c56bb87e4d341f4b4";
const IMG_API = "https://image.tmdb.org/t/p/w1280";
const SEARCH_API = "https://api.themoviedb.org/3/search/movie?&api_key=6c509f03a0794e8c56bb87e4d341f4b4&query=";
function App() {
  const [ movies, setMovies ] = useState ([]);
  useEffect(() => {
    fetch(FEATURE_API).then(res => res.json())
    .then(data => {
      console.log(data);
      setMovies(data.results);
    });

  }, []);

  return (
  <div>
    {movies.length > 0 && 
      movies.map((movie) => <Movie key={movie.
      id} data={movie} />)}

  </div>
  );




}

export default App;
